import React, { Component } from 'react'
import SongService from '../services/SongService'

class ViewSongsComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
                employees: []
        }

    }



    componentDidMount(){

        SongService.getSongs().then(
            (res) => {
            this.setState({ songs: res.data});
            console.log('What happened? ' + res.data);
        }
        )
        /*.catch((err: any) => {
            var returnJson = [{"id":1,"firstName":"Siva","lastName":"Kumar","emailId":"WorkShiva1@gmail.com"},{"id":2,"firstName":"Siva","lastName":"Krish","emailId":"WorkShiva2@gmail.com"},{"id":3,"firstName":"Krishna","lastName":"Kumar","emailId":"Krish1@gmail.com"},{"id":4,"firstName":"Krishna","lastName":"Shiva","emailId":"Krish2@gmail.com"}];
            console.log('What happened? ' + err.res.data);
            this.setState({ employees: returnJson});
        }
        );*/
    }

    viewSongs(){
        this.props.history.push('/song');
    }

    render() {
        return (
            <div>
                 <h2 className="text-center">Songs List</h2>
                 <div className = "row">
                    <button className="btn btn-primary" onClick={this.viewSongs}> View Songs</button>
                 </div>
                 <br></br>
                 <div className = "row">
                        <table className = "table table-striped table-bordered">

                            <thead>
                                <tr>
                                    <th> Song ID </th>
                                    <th> Songwriter</th>
                                    <th> Song Name</th>

                                </tr>
                            </thead>
                            <tbody>

                            {
                                    this.state.songs.map(
                                        employee => 
                                        <tr key = {employee.id}>
                                             <td> { employee.firstName} </td>   
                                             <td> {employee.lastName}</td>
                                             <td> {employee.emailId}</td>
                                             <td>
                                                 <button onClick={ () => this.editEmployee(employee.id)} className="btn btn-info">Update </button>
                                                 <button style={{marginLeft: "10px"}} onClick={ () => this.deleteEmployee(employee.id)} className="btn btn-danger">Delete </button>
                                                 <button style={{marginLeft: "10px"}} onClick={ () => this.viewEmployee(employee.id)} className="btn btn-info">View </button>
                                             </td>
                                        </tr>
                                    )
                                }


                            </tbody>
                        </table>

                 </div>

            </div>
        )
    }
}

export default ViewSongsComponent
